import React from 'react';
import '../App.css'; 
import Fullcalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { useRef, useEffect } from 'react';
import { useContext } from 'react';
import { EventContext } from './EventContext';


function MonthPage() {
    const calendarRef = useRef(null);
    const {events, setEvents} = useContext(EventContext);

    useEffect(() => {
        const storedEvents = JSON.parse(localStorage.getItem('calendarEvents'));
        if (storedEvents) {
            setEvents(storedEvents)
        }
    }, [setEvents]);


    const handleEventAdd = (event) => {
        const newEvents = [...events, event];
        setEvents(newEvents);
        localStorage.setItem('calendarEvents', JSON.stringify(newEvents));
    }


    const handleEventClick = (info) => {
        const event = info.event;
        const choice = prompt(`Do you want to edit or delete the event "${event.title}"?\nType 'Edit' or 'Delete'`);

        if (choice === 'Edit') {
          const newTitle = prompt('Enter the new title for the event:', event.title);
          if (newTitle) {
            event.setProp('title', newTitle);
            const updatedEvents = events.map(evt => evt.id === event.id ? {...evt, title: newTitle} : evt);
            setEvents(updatedEvents);
            localStorage.setItem('calendarEvents', JSON.stringify(updatedEvents));
            alert('Event updated. Now, update your database...');
          } else {
            alert('Event title cannot be empty.');
          }
        } else if (choice === 'Delete') {

          const updatedEvents = events.filter(evt => evt.id !== parseInt(event.id, 10));
          event.remove();
          console.log(event.id);

          setEvents(updatedEvents);

          localStorage.setItem('calendarEvents', JSON.stringify(updatedEvents));
          alert('Event deleted. Now, update your database...');
        }
      };

      const getRandomColor = (dayOfYear) => {
        // Use the day of the year to generate a color
        const hue = ((dayOfYear * 137.508) % 360) / 360; // Arbitrary constant
        const saturation = 0.8;
        const lightness = 0.5;
        return `hsl(${Math.floor(hue * 360)}, ${Math.floor(saturation * 100)}%, ${Math.floor(lightness * 100)}%)`;
    };

    const eventRender = (info) => {
        // Assign a color to the event based on its start date
        const start = new Date(info.event.start);
        const dayOfYear = Math.floor((Date.UTC(start.getFullYear(), start.getMonth(), start.getDate()) - Date.UTC(start.getFullYear(), 0, 0)) / 86400000);
        const color = getRandomColor(dayOfYear);
        info.el.style.backgroundColor = color;
    };


    return (

        <div>


            <h1>Month</h1>
            <Fullcalendar
                ref={calendarRef}
                plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                headerToolbar={{
                    center: 'addEventButton'
                  }}
                  customButtons={{
                    addEventButton: {
                      text: 'add event...',
                      click: function() {
                        var dateStr = prompt('Enter a date in YYYY-MM-DD format');
                        var date = new Date(dateStr + 'T00:00:00'); // will be in local time

                        if (!isNaN(date.valueOf())) { // valid?
                            var title = prompt('Enter the event title:');
                            var id = Date.now();
                            console.log(id);

                                const calendarApi = calendarRef.current.getApi()
                                const newEvent = {
                                    id: id,
                                    title: title,
                                    start: date,
                                    allDay: true
                                }
                          calendarApi.addEvent(newEvent);
                          handleEventAdd(newEvent);
                          alert('Great. Now, update your database...');
                        } else {
                          alert('Invalid date.');
                        }
                      }
                    }
                  }}
                  events={events}
                  eventClick={handleEventClick}
                  eventRender={eventRender}

            />

            <div id='calendar'></div>

        </div>
    );
}

export default MonthPage;
