import './App.css';
import Sidebar from './Components/sidebar';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './Pages/Home';
import { EventProvider } from './Pages/EventContext';
import MonthPage from './Pages/MonthPage';
import WeekPage from './Pages/Week';
import Notes from './Pages/Notes';
import { WeekProvider } from './Pages/WeekContext';
import { useState } from 'react';

import { ChakraProvider, theme, Alert, AlertIcon, AlertTitle, AlertDescription, Button, Container, Box} from '@chakra-ui/react';

function notifyUser (notificationText = "Thank you for enabling notifications") {
  if(!("Notification" in window)) {
    alert("Browser does not support notifications");
  } else if (Notification.permission === "granted") {
    const notification = new Notification(notificationText);
  } else if(Notification.permission !== "denied") {
    Notification.requestPermission().then((permission) => {
      if(permission === "granted") {
        const notification = new Notification(notificationText);
      }
    });
  }
}

function enableNotifsAndClose () {

}

function disableNotifsAndClose () {

}


function App() {
  const [userResponded, setUserResponded] = useState(false);

  return (!(userResponded) && !(Notification.permission === "granted")) ? (

    <EventProvider>
    <WeekProvider>
      <Router>

        <div className="App">
          <Sidebar />
            <div className="PageContent">
              <Routes>
                <Route path="/home" element={<Home />} />
                <Route path="/month" element={<MonthPage />} />
                <Route path="/week" element={<WeekPage />} />
                <Route path="/notes" element={<Notes />} />
              </Routes>
            </div>
        </div>

      </Router>

    </WeekProvider>
    </EventProvider>
  ) : (Notification.permission === "granted") ? (
    <ChakraProvider theme={theme}>

    </ChakraProvider>
  ) :

    <EventProvider>
      <WeekProvider>
        <Router>

          <div className="App">
            <Sidebar />
              <div className="PageContent">
                <Routes>
                  <Route path="/home" element={<Home />} />
                  <Route path="/month" element={<MonthPage />} />
                  <Route path="/week" element={<WeekPage />} />
                  <Route path="/notes" element={<Notes />} />
                </Routes>
              </div>
          </div>

        </Router>
      </WeekProvider>
    </EventProvider>


}

export default App;
